import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { ChevronDown, Menu, X, Sun, Moon } from "lucide-react";
import "../styles/Navbar.css";

const Navbar = ({ isDarkTheme, toggleTheme }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [isAcademicsOpen, setIsAcademicsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Scroll effect for shadow background
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav
      className={`navbar ${isDarkTheme ? "dark-theme" : "light-theme"} ${
        scrolled ? "scrolled" : ""
      }`}
    >
      <div className="navbar-container">
        <div className="navbar-content">
          {/* ===== LEFT: LOGO ===== */}
          <Link to="/" className="navbar-logo">
            <img src="./logo.jpg" className="logo" alt="logo" />
            <div className="logoname">
              <p className="first">Men Legum</p>
              <p className="last">Sanctitas</p>
            </div>
          </Link>

          {/* ===== CENTER: MENU ===== */}
          <div className="navbar-menu">
            <a href="#about">About Us</a>
            <a href="#practice">Practice Areas</a>
            <a href="#people">Our People</a>
            <Link to="/careers">Careers</Link>

            {/* Academics Dropdown */}
            <div
              className="navbar-dropdown"
              onMouseEnter={() => setIsAcademicsOpen(true)}
              onMouseLeave={() => setIsAcademicsOpen(false)}
            >
              <button className="navbar-dropdown-btn">
                Academics <ChevronDown size={16} />
              </button>
              {isAcademicsOpen && (
                <div className="navbar-dropdown-menu">
                  <Link to="/blogs">Blogs</Link>
                  <Link to="/newsletters">Newsletters</Link>
                  <Link to="/publications">Publications</Link>
                  <Link to="/events">Events</Link>
                </div>
              )}
            </div>

            <a href="#form">Contact Us</a>
          </div>

          {/* ===== RIGHT: ACTIONS ===== */}
          <div className="navbar-actions">
            <button onClick={toggleTheme} className="navbar-theme-btn">
              {isDarkTheme ? <Sun size={20} /> : <Moon size={20} />}
            </button>

            <button
              onClick={() => setIsOpen(!isOpen)}
              className="navbar-mobile-toggle"
            >
              {isOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* ===== MOBILE MENU ===== */}
        {isOpen && (
          <div className="navbar-mobile-menu">
            <a href="#about" onClick={() => setIsOpen(false)}>
              About Us
            </a>
            <a href="#practice" onClick={() => setIsOpen(false)}>
              Practice Areas
            </a>
            <a href="#people" onClick={() => setIsOpen(false)}>
              Our People
            </a>
            <Link to="/careers" onClick={() => setIsOpen(false)}>
              Careers
            </Link>

            <details className="mobile-dropdown">
              <summary>Academics</summary>
              <div className="mobile-dropdown-menu">
                <Link to="/blogs" onClick={() => setIsOpen(false)}>
                  Blogs
                </Link>
                <Link to="/newsletters" onClick={() => setIsOpen(false)}>
                  Newsletters
                </Link>
                <Link to="/publications" onClick={() => setIsOpen(false)}>
                  Publications
                </Link>
                <Link to="/events" onClick={() => setIsOpen(false)}>
                  Events
                </Link>
              </div>
            </details>

            <a href="#form" onClick={() => setIsOpen(false)}>
              Contact Us
            </a>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
