import React, { useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import axios from 'axios';
import '../styles/Careers.css';

const Careers = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    residentialAddress: '',
    college: '',
    course: '',
    yearOfStudy: '',
    coverLetter: ''
  });

  const [cvFile, setCvFile] = useState(null);
  const [fileName, setFileName] = useState('No file chosen');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setCvFile(file);
      setFileName(file.name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    const data = new FormData();
    Object.keys(formData).forEach(key => {
      data.append(key, formData[key]);
    });
    data.append('cv', cvFile);

    try {
      await axios.post('http://localhost:5000/api/applications', data, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setMessage('Application submitted successfully!');

      // Reset form
      setFormData({
        fullName: '',
        email: '',
        phoneNumber: '',
        residentialAddress: '',
        college: '',
        course: '',
        yearOfStudy: '',
        coverLetter: ''
      });
      setCvFile(null);
      setFileName('No file chosen');
    } catch (error) {
      setMessage('Error submitting application. Please try again.');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="internship-container">
      <div className="form-wrapper">
        <h1 className="form-title">Internship Application</h1>
        <p className="form-subtitle">We are looking for talented and motivated students to join our team.</p>

        {/* Back button */}
        <button className="back-btn" onClick={() => navigate('/')}>
          &larr; Back
        </button>

        <form onSubmit={handleSubmit} className="application-form">
          {/* Personal Information */}
          <div className="form-section">
            <h2 className="section-title">Personal Information</h2>
            <div className="form-row">
              <div className="form-group">
                <label>Full Name</label>
                <input
                  type="text"
                  name="fullName"
                  placeholder="John Doe"
                  value={formData.fullName}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email Address</label>
                <input
                  type="email"
                  name="email"
                  placeholder="john.doe@example.com"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Phone Number</label>
                <input
                  type="tel"
                  name="phoneNumber"
                  placeholder="+91 98765 43210"
                  value={formData.phoneNumber}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Residential Address</label>
                <input
                  type="text"
                  name="residentialAddress"
                  placeholder="123 Law Street, Justice City"
                  value={formData.residentialAddress}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          {/* Educational Qualifications */}
          <div className="form-section">
            <h2 className="section-title">Educational Qualifications</h2>
            <div className="form-row">
              <div className="form-group">
                <label>College/University</label>
                <input
                  type="text"
                  name="college"
                  placeholder="National Law School"
                  value={formData.college}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Course</label>
                <input
                  type="text"
                  name="course"
                  placeholder="B.A. LL.B (Hons)"
                  value={formData.course}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Year of Study</label>
                <input
                  type="text"
                  name="yearOfStudy"
                  placeholder="e.g., 3rd Year"
                  value={formData.yearOfStudy}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          {/* Documents */}
          <div className="form-section">
            <h2 className="section-title">Documents</h2>
            <div className="form-group">
              <label>CV / Resume</label>
              <div className="file-input-wrapper">
                <input
                  type="file"
                  id="cv-upload"
                  accept=".pdf,.doc,.docx"
                  onChange={handleFileChange}
                  required
                />
                <label htmlFor="cv-upload" className="file-input-label">
                  Choose file
                </label>
                <span className="file-name">{fileName}</span>
              </div>
            </div>

            <div className="form-group">
              <label>Cover Letter (Optional)</label>
              <textarea
                name="coverLetter"
                placeholder="Tell us why you want to intern at MLS&Co..."
                value={formData.coverLetter}
                onChange={handleChange}
                rows="6"
              />
            </div>
          </div>

          {message && (
            <div className={`message ${message.includes('success') ? 'success' : 'error'}`}>
              {message}
            </div>
          )}

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Submitting...' : 'Submit Application'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Careers;
