import React, { useState } from 'react';
import { 
  Gavel, Building2, Briefcase, Scale, Users, Lightbulb, 
  Building, Home, Percent, Laptop, UserCheck 
} from 'lucide-react';
import '../styles/PracticeAreas.css';

const PracticeAreas = () => {
  const [selectedArea, setSelectedArea] = useState(null);

  const areas = [
    { icon: Gavel, title: 'Arbitration', description: 'We handle domestic and international arbitration cases, providing strategic representation and dispute resolution services for our clients.' },
    { icon: Building2, title: 'Banking & Finance', description: 'Our lawyers advise financial institutions and corporations on complex finance transactions and regulatory compliance.' },
    { icon: Briefcase, title: 'Corporate & Commercial', description: 'We assist with mergers, acquisitions, and business transactions, ensuring seamless corporate governance and compliance.' },
    { icon: Scale, title: 'Dispute Resolution', description: 'Our team resolves disputes efficiently through litigation, mediation, and negotiation, protecting clients’ interests.' },
    { icon: Users, title: 'Employment Law', description: 'We guide employers and employees on labor contracts, disputes, and employment law compliance.' },
    { icon: Lightbulb, title: 'Intellectual Property', description: 'We protect your IP assets through registration, enforcement, and strategic portfolio management.' },
    { icon: Building, title: 'Projects, Energy & Infrastructure', description: 'We advise on major infrastructure and energy projects, covering financing, structuring, and execution.' },
    { icon: Home, title: 'Real Estate', description: 'Our expertise covers real estate transactions, property laws, and project development advisory.' },
    { icon: Gavel, title: 'Restructuring & Insolvency', description: 'We help companies navigate insolvency, restructuring, and debt recovery under complex legal frameworks.' },
    { icon: Percent, title: 'Taxation', description: 'We provide expert tax planning, compliance, and dispute resolution services for businesses and individuals.' },
    { icon: Laptop, title: 'Technology, Media & Telecommunications', description: 'Our team offers legal solutions in data protection, digital media, and telecom regulations.' },
    { icon: UserCheck, title: 'White Collar Crime', description: 'We provide defense and advisory services in corporate fraud, money laundering, and compliance investigations.' },
  ];

  return (
    <section id="practice" className="practice-areas">
      <div className="practice-areas-container">
        <div className="practice-areas-header">
          <h2 className="practice-areas-title">Our Practice Areas</h2>
          <p className="practice-areas-subtitle">
            We offer a comprehensive range of legal services to meet the diverse needs of our clients.
          </p>
        </div>

        {/* Cards Grid */}
        <div className="practice-areas-grid">
          {areas.map((area, index) => {
            const Icon = area.icon;
            return (
              <div 
                key={index} 
                className="practice-card"
                onClick={() => setSelectedArea(area)}
              >
                <Icon className="practice-card-icon" size={40} />
                <h3 className="practice-card-title">{area.title}</h3>
                <span className="hover-text">Click to view details</span>
              </div>
            );
          })}
        </div>

        {/* Popup Modal */}
        {selectedArea && (
          <div className="practice-modal-overlay" onClick={() => setSelectedArea(null)}>
            <div className="practice-modal" onClick={(e) => e.stopPropagation()}>
              <button className="close-btn" onClick={() => setSelectedArea(null)}>✕</button>
              <div className="modal-icon">
                {React.createElement(selectedArea.icon, { size: 100, color: '#FDB913' })}
              </div>
              <h2>{selectedArea.title}</h2>
              <p>{selectedArea.description}</p>
              
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default PracticeAreas;
