import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Add this import
import { ChevronDown, Menu, X, Sun, Moon } from "lucide-react";
import "../styles/Navbar.css"
import b_1 from "../b_1.png"

const Navbar = () => {
  // ... your existing state

  return (
    <nav className={`navbar ${isDarkTheme ? "dark-theme" : "light-theme"} ${scrolled ? "scrolled" : ""}`}>
      <div className="navbar-container">
        <div className="navbar-content">
          <Link to="/" className="navbar-logo"> {/* Changed from <a href="#home"> */}
            <img src={b_1} className="logo" alt="logo" />
            <div className="logoname">
              <p className="first">MLS & Co</p>
              <p className="last">LAW FIRM</p>
            </div>
          </Link>
          
          <div className="navbar-menu">
            <Link to="/#about">About Us</Link>
            <Link to="/#practice">Practice Areas</Link>
            <Link to="/#people">Our People</Link>
            <Link to="/careers">Careers</Link> {/* This will navigate to careers page */}
            
            <div className="navbar-dropdown" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
              <button className="navbar-dropdown-btn">
                Academics <ChevronDown size={16} />
              </button>
              {isAcademicsOpen && (
                <div className="navbar-dropdown-menu" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                  <Link to="/#blogs">Blogs</Link>
                  <Link to="/#newsletters">Newsletters</Link>
                  <Link to="/#publications">Publications</Link>
                  <Link to="/#events">Events</Link>
                </div>
              )}
            </div>
            
            <Link to="/#contact">Contact Us</Link>
          </div>
          
          {/* ... rest of your navbar code */}
        </div>
        
        {/* Update mobile menu links too */}
        {isOpen && (
          <div className="navbar-mobile-menu">
            <Link to="/#about" onClick={() => setIsOpen(false)}>About Us</Link>
            <Link to="/#practice" onClick={() => setIsOpen(false)}>Practice Areas</Link>
            <Link to="/#people" onClick={() => setIsOpen(false)}>Our People</Link>
            <Link to="/careers" onClick={() => setIsOpen(false)}>Careers</Link>
            {/* ... rest of mobile menu */}
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
