import React, { useState } from 'react';
import '../styles/Hero.css';

const Hero = () => {
  const [isOpen, setIsOpen] = useState(true);
  return (
    <section className="hero">
      <button className="section-toggle-btn" onClick={() => setIsOpen(!isOpen)}>
        {isOpen ? 'Hide Section' : 'Show Section'}
      </button>
      {isOpen && (
        <div className="hero-content">
          <h1 className="hero-title">
            Legal Excellence. Redefined.
          </h1>
          <p className="hero-subtitle">
            A premier Indian law firm renowned for its integrity, expertise, and client-centric approach.
          </p>
          <button className="hero-cta">
            Discover More
          </button>
        </div>
      )}
    </section>
  );
};

export default Hero;
