import React, { useState } from 'react';
import { Sun, Moon } from 'lucide-react';
import '../styles/Hero.css';

const Hero = () => {
  const [isLight, setIsLight] = useState(false);
  return (
    <section className={`hero${isLight ? ' light' : ''}`}>
      <button className="section-theme-btn" onClick={() => setIsLight(!isLight)}>
        {isLight ? <Moon size={18} /> : <Sun size={18} />}
      </button>
      <div className="hero-content">
          <h1 className="hero-title">
            Legal Excellence. Redefined.
          </h1>
          <p className="hero-subtitle">
            A premier Indian law firm renowned for its integrity, expertise, and client-centric approach.
          </p>
          <button className="hero-cta">
            Discover More
          </button>
        </div>
    </section>
  );
};

export default Hero;
