import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

const Navbar = ({ isDarkTheme, toggleTheme }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleScroll = (id) => {
    if (location.pathname !== "/") {
      // If not on home, navigate to home first
      navigate("/");
      setTimeout(() => {
        document.getElementById(id)?.scrollIntoView({ behavior: "smooth" });
      }, 300); // wait for page to render
    } else {
      document.getElementById(id)?.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <nav className={`navbar ${isDarkTheme ? "dark" : "light"}`}>
      <div className="logo" onClick={() => navigate("/")}>
        MLS&Co
      </div>

      <ul className="nav-menu">
        <li onClick={() => handleScroll("about")}>About</li>
        <li onClick={() => handleScroll("practice")}>Practice Areas</li>
        <li onClick={() => handleScroll("people")}>Our People</li>
        <li onClick={() => handleScroll("contact")}>Contact Us</li>
      </ul>
    </nav>
  );
};

export default Navbar;
