import React, { useState } from 'react';
import { Gavel, Building2, Briefcase, Scale, Users, Lightbulb, Building, Home, Percent, Laptop, UserCheck } from 'lucide-react';
import '../styles/PracticeAreas.css';

const PracticeAreas = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);

  const areas = [
    { icon: Gavel, title: 'Arbitration', description: 'We provide expert legal representation in domestic and international arbitration cases.' },
    { icon: Building2, title: 'Banking & Finance', description: 'Our team advises on corporate finance, restructuring, and financial regulations.' },
    { icon: Briefcase, title: 'Corporate & Commercial', description: 'We assist in mergers, acquisitions, joint ventures, and corporate governance.' },
    { icon: Scale, title: 'Dispute Resolution', description: 'We specialize in efficient dispute resolution through mediation and litigation.' },
    { icon: Users, title: 'Employment Law', description: 'Advising employers and employees on contracts, disputes, and labor laws.' },
    { icon: Lightbulb, title: 'Intellectual Property', description: 'We protect and enforce intellectual property rights across industries.' },
    { icon: Building, title: 'Projects, Energy & Infrastructure', description: 'Legal solutions for large-scale infrastructure and energy projects.' },
    { icon: Home, title: 'Real Estate', description: 'Comprehensive legal services for property transactions and development.' },
    { icon: Gavel, title: 'Restructuring & Insolvency', description: 'We guide companies through financial distress and insolvency procedures.' },
    { icon: Percent, title: 'Taxation', description: 'Advising individuals and corporations on direct and indirect tax matters.' },
    { icon: Laptop, title: 'Technology, Media & Telecommunications', description: 'Legal expertise in data privacy, media, and telecom regulations.' },
    { icon: UserCheck, title: 'White Collar Crime', description: 'Defense and advisory in cases involving corporate fraud and compliance.' },
  ];

  return (
    <section id="practice" className="practice-areas">
      <div className="practice-areas-container">
        <div className="practice-areas-header">
          <h2 className="practice-areas-title">Our Practice Areas</h2>
          <p className="practice-areas-subtitle">
            We offer a comprehensive range of legal services to meet the diverse needs of our clients.
          </p>
        </div>
        <div className="practice-areas-grid">
          {areas.map((area, index) => {
            const Icon = area.icon;
            return (
              <div
                key={index}
                className="practice-card"
                onMouseEnter={() => setHoveredIndex(index)}
                onMouseLeave={() => setHoveredIndex(null)}
              >
                <Icon className="practice-card-icon" size={40} />
                <h3 className="practice-card-title">{area.title}</h3>

                {hoveredIndex === index && (
                  <div className="practice-popup">
                    <p>{area.description}</p>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default PracticeAreas;
