import React, { useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';
import '../styles/Careers.css'

const Careers = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    residentialAddress: '',
    college: '',
    course: '',
    yearOfStudy: '',
    coverLetter: ''
  });

  const [cvFile, setCvFile] = useState(null);
  const [fileName, setFileName] = useState('No file chosen');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setCvFile(file);
      setFileName(file.name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    const data = new FormData();
    Object.keys(formData).forEach(key => {
      data.append(key, formData[key]);
    });
    data.append('cv', cvFile);

    try {
      const response = await axios.post('http://localhost:5000/api/applications', data, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setMessage('Application submitted successfully!');
      setFormData({
        fullName: '',
        email: '',
        phoneNumber: '',
        residentialAddress: '',
        college: '',
        course: '',
        yearOfStudy: '',
        coverLetter: ''
      });
      setCvFile(null);
      setFileName('No file chosen');
    } catch (error) {
      setMessage('Error submitting application. Please try again.');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="internship-container">
      <div className="form-wrapper">
        {/* Back Button */}
        <Link to="/" className="back-btn">
          &larr; Back
        </Link>

        <h1 className="form-title">Internship Application</h1>
        <p className="form-subtitle">We are looking for talented and motivated students to join our team.</p>

        <form onSubmit={handleSubmit} className="application-form">
          {/* Personal Information */}
          <div className="form-section">
            <h2 className="section-title">Personal Information</h2>
            <div className="form-row">
              {/* ... same inputs as before */}
            </div>
          </div>

          {/* Educational Qualifications */}
          <div className="form-section">
            <h2 className="section-title">Educational Qualifications</h2>
            <div className="form-row">
              {/* ... same inputs as before */}
            </div>
          </div>

          {/* Documents */}
          <div className="form-section">
            <h2 className="section-title">Documents</h2>
            {/* ... same inputs as before */}
          </div>

          {message && (
            <div className={`message ${message.includes('success') ? 'success' : 'error'}`}>
              {message}
            </div>
          )}

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Submitting...' : 'Submit Application'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Careers;
